INSERT INTO `books` (`id`, `uuid`, `title`, `slug`, `description`, `cover_image`, `duration`, `author_id`, `narrator_id`, `category_id`, `price`, `is_free`, `is_featured`, `is_active`, `total_views`, `average_rating`, `published_at`, `created_at`, `updated_at`) VALUES
(1, 'd32a357a-b4ee-40a9-a248-9ac53dca2ec3', 'کتاب صوتی هری پاتر و سنگ جادو جی کی رولینگ آوانامه', 'harry-potter-and-the-sorcerers-stone-audiobook-by-j-k-rowling-avaname', 'هری پاتر کیست؟\r\n\r\nهری پاتر، شخصیت اصلی داستان، پسر نوجوان یازده ساله‌ای است که در ابتدای داستان در کمد زیر پله خانه دورسلی‌ها زندگی می‌کند، خانواده‌ای که شامل خاله، شوهرخاله و پسرخاله‌ی هم سن و سال چاق و منفورش است. علیرغم تلاش دورسلی‌ها برای پنهان کردن هویت اصلی خانواده پاتر و استعدادهای جادوگری‌شان، بالاخره هری به دنیای جالب و پر از هیجان جادوگری، از جمله هاگوارتز، مدرسه جادوگری، راه پیدا می‌کند و در آنجا با دو دوست صمیمی خود، رون و هرمیون، آشنا می‌شود و دوران پر فراز و نشیبی را با چالش‌های فراوان سپری می‌کند؛ چالش‌هایی از جمله رویارویی با لرد ولدمورت که حتی بردن نامش بر اندام‌ها لرزه می‌اندازد. ولدمورت، جادوگر منحوس و پلیدی که افراد زیادی را به قتل رسانده، نتوانسته هری را در سن خردسالی از پا درآورد و تنها زخم صاعقه مانندی را بر روی پیشانی‌اش برجای گذاشته است. چنین است که هری پاتر به \"پسری که زنده ماند\" معروف شده است.\r\n\r\n\r\n\r\nجی.کی.رولینگ\r\n\r\nجوآن رولینگ، ملقب به جی.کی.رولینگ، نویسنده زن بریتانیایی این کتاب، توسط مجله تایم در سال 2007، بعد از ولادیمیر پوتین، به عنوان تأثیرگذارترین فرد سال انتخاب شد. او مدعی است ایده‌ی هری پاتر زمانی به ذهنش خطور کرده‌است که در ایستگاه قطار منتظر ایستاده بوده. نقطه قوت این اثر که آن را جزو پرفروش‌ترین کتاب‌های جهان کرده این است که به مخاطبان خود(چه نوجوان و چه بزرگسال) دنیای بسیار جذاب دیگر ولی «پنهان» را معرفی می‌کند که ممکن است هر کسی به آن تعلق داشته باشد.\r\n\r\n\r\n\r\nسنگ جادو\r\n\r\nجی.کی.رولینگ در هر یک از مجموعه کتاب‌های هری پاتر ماجراهای هر سال تحصیلی در هاگوارتز را روایت می‌کند که از تابستان و تعطیلات قبل از مدرسه شروع می‌شود و در انتهای سال تحصیلی آن را به پایان می‌رساند. هری پاتر و سنگ جادو که نام جلد اول این مجموعه است، شامل ماجرا‌ها و اتفاقاتی است که طی سال اول تحصیلی هری پاتر رخ می‌دهد. او با شجاعت خود و با کمک هوش هرمیون، وفاداری رون و خرد دامبلدور (مدیر مدرسه) می‌کوشد تا این سنگ جادو به دست افراد نادرست نیافتد.\r\n\r\n\r\n\r\nهری پاتر را بخوانیم، ببینیم و بشنویم!\r\n\r\nبا وجود جذابیت فراوان و پرطرفدار بودن فیلم‌های هری پاتر که توسط کمپانی برادران وارنر ساخته شده است، متوجه تفاوت‌هایی با متن کتاب می‌شویم. این تفاوت‌ها شامل حذف بعضی جزئیات و شخصیت‌ها و حتی بعضی ماجراها است که باعث شده اکثر طرفداران این مجموعه، داستان مکتوب را نسبت به فیلم‌های ساخته شده کامل‌تر، دقیق‌تر و جذاب‌تر بدانند. بنابراین با شنیدن مجموعه کتاب‌های صوتی هری پاتر علاوه بر اینکه از جزئیات باخبر خواهید شد بلکه می‌توانید با اجرای شنیدنی و نمایشی آرمان سلطان زاده همراه با افکت‌های صوتی و موسیقی، بیشتر درگیر غم‌ها و شادی‌ها و هیجانات شوید و و از داستان لذت بیشتر ببرید.', '/assets/images/books/harry-potter-and-the-sorcerers-stone-audiobook-by-j-k-rowling-avaname_1762290168.webp', 0, 1, 3, 4, 0.00, 1, 0, 1, 0, 0.00, NULL, '2025-11-04 21:02:48', '2025-11-04 21:02:48');

